```{.r .cell-code .hidden}
#| echo: false
#| label: tbl-exp1-statsResults-glmm
#| tbl-cap: "Experiment 1. Summary of the word priming results based on the 2x3 GLMM model, with the same fixed and random effects as the 2x2 GLMM model, but including the non-word condition. *Legend.* MOP: magnitude of priming."
#| tbl-pos: 'h'

exp1_summary.results_glmm <- bind_rows(exp1_estimates_glmm, exp1_estimates_glmm_fae)

exp1_summary.results_glmm %>%
  mutate(z.ratio = round(z.ratio, 2),
         p.value = "<.0001") %>%
  gt() %>%
  cols_label(
    contains("CI") ~ "95% CI",
    contains("emmean") ~ "mean",
    contains("SE") ~ "SE"
  ) %>%
  tab_spanner(
    label = "unrelated RT",
    columns = c(2:4)
  ) %>%
  tab_spanner(
    label = "repetition RT",
    columns = c(5:7)
  ) %>%
  tab_spanner(
    label = 'priming effects',
    columns = c(8:10)
  ) %>%
   sub_missing(
    missing_text = " "
  )
